def course_category(course):
    if course < 0:
        return None
    if course <= 4:
        return 'Large'
    if course <= 13:
        return 'Medium'
    return 'Small'

def add_course_category(performances):
    performances.loc[:, 'CourseSize'] = performances['course_id'].apply(course_category)
    return performances

performances_by_size = add_course_category(performances)

