def parse(lines):
    block = []
    while lines :
        if lines[0].startswith('If'):
            bl = ' '.join(lines.pop(0).split()[1:]).replace('(', '').replace(')', '')
            block.append({'name': get_feature_name(bl), 'children': parse(lines)})

